import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import SignUp from './components/SignUp';
import SignIn from './components/SignIn';
import Dashboard from './components/Dashboard';
import CreateForm from './components/CreateForm';
import './App.css';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(!!localStorage.getItem('token'));

  // Listen for changes in localStorage when the token is set
  useEffect(() => {
    const token = localStorage.getItem('token');
    setIsAuthenticated(!!token);  // Update the authentication state
  }, []);

  return (
    <Router>
      <div className="App">
        <Routes>
          <Route path="/" element={isAuthenticated ? <Navigate to="/home" /> : <SignUp />} />
          <Route path="/signin" element={isAuthenticated ? <Navigate to="/home" /> : <SignIn />} />
          <Route path="/home" element={isAuthenticated ? <HomePage /> : <Navigate to="/signin" />} />
          <Route path="/dashboard" element={isAuthenticated ? <Dashboard /> : <Navigate to="/signin" />} />
        </Routes>
      </div>
    </Router>
  );
}

// HomePage component that includes the Dashboard and CreateForm components
const HomePage = () => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(false); // Toggle sidebar visibility

  // Function to toggle sidebar visibility
  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  return (
    <div>
      <div className="header">
        <h1>Forms</h1>
        <div className="hamburger-menu" onClick={toggleSidebar}>
          &#9776; {/* Hamburger Icon */}
        </div>
      </div>
      <div className={`dashboard-container ${isSidebarOpen ? 'open' : ''}`}>
        <Dashboard isSidebarOpen={isSidebarOpen} /> {/* Pass down sidebar state */}
        <CreateForm />
      </div>
    </div>
  );
};

export default App;
