import React, { useState } from 'react';
import './Header.css'; // Create this file for header styling

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const handleSignOut = () => {
    localStorage.removeItem('token'); // Remove token from localStorage
    window.location.href = '/signin'; // Redirect to SignIn page after signout
  };

  return (
    <header className="header">
      <div className="header-left">
        <div className="logo">Google Forms Clone</div>
        <div className="hamburger-menu" onClick={() => setIsMenuOpen(!isMenuOpen)}>
          &#9776; {/* Hamburger icon */}
        </div>
      </div>
      <div className="user-profile">
        <img
          className="profile-pic"
          src="https://via.placeholder.com/40" /* Replace with real image source */
          alt="User"
        />
        <div className="user-name">John Doe</div>
      </div>

      {isMenuOpen && (
        <div className="dropdown-menu">
          <button onClick={() => window.location.href = '/settings'}>Settings</button>
          <button onClick={handleSignOut}>Sign Out</button>
        </div>
      )}
    </header>
  );
};

export default Header;
