import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const SignUp = () => {
  const [formData, setFormData] = useState({ username: '', email: '', password: '' });
  const { username, email, password } = formData;
  const navigate = useNavigate();

  const [isSubmitting, setIsSubmitting] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');

  useEffect(() => {
    console.log("SignUp Component Rendered");
  }, []);

  const onChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
    console.log(`Field changed: ${e.target.name} = ${e.target.value}`); // Log field changes
  };

  const validateForm = () => {
    if (!username || !email || !password) {
      setErrorMessage('All fields are required.');
      return false;
    }
    // Add more validation logic if necessary (e.g., email format, password strength)
    return true;
  };

  const onSubmit = async (e) => {
    e.preventDefault();

    // Validate the form before submission
    if (!validateForm()) {
      toast.error(errorMessage, { position: toast.POSITION.TOP_RIGHT });
      return;
    }

    setIsSubmitting(true);
    console.log("Submitting form with data:", formData); // Log form data on submit

    try {
      const res = await axios.post('http://localhost:5000/api/auth/register', formData);
      console.log("Registration success:", res.data); // Log response from the server
      localStorage.setItem('token', res.data.token);

      // Display success message
      toast.success('Registration successful!', {
        position: toast.POSITION.TOP_RIGHT
      });

      // Redirect to home page after 1.5 seconds
      setTimeout(() => {
        navigate('/home');
      }, 1500);
    } catch (error) {
      console.error('Registration error:', error.response?.data || error.message);

      // Display detailed error message if available
      toast.error(error.response?.data?.message || 'Registration failed. Please try again.', {
        position: toast.POSITION.TOP_RIGHT
      });
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div>
      <ToastContainer />
      <h2>Sign Up</h2>

      {/* Loading Indicator */}
      {isSubmitting && <p>Submitting... Please wait.</p>}

      <form onSubmit={onSubmit}>
        <input
          type="text"
          name="username"
          placeholder="Username"
          value={username}
          onChange={onChange}
          required
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={email}
          onChange={onChange}
          required
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          value={password}
          onChange={onChange}
          required
        />
        <button type="submit" disabled={isSubmitting}>
          {isSubmitting ? 'Registering...' : 'Register'}
        </button>
      </form>

      <p>Already registered? <a href="/signin">Sign In</a></p>
    </div>
  );
};

export default SignUp;
