import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom'; // Hook to access formId from URL

const FormResponse = () => {
  const { formId } = useParams(); // Extract formId from URL
  const [form, setForm] = useState(null);
  const [answers, setAnswers] = useState({});

  useEffect(() => {
    console.log("Fetching form with ID:", formId); // Add this to check if formId is correct
    const fetchForm = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/forms/${formId}`);
        setForm(response.data);
      } catch (error) {
        console.error('Error fetching form:', error);
      }
    };

    fetchForm();
  }, [formId]);


  const handleInputChange = (questionId, answer) => {
    setAnswers({
      ...answers,
      [questionId]: answer,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post(`http://localhost:5000/api/forms/${formId}/responses`, { answers });
      alert('Responses submitted successfully');
    } catch (error) {
      console.error('Error submitting responses:', error);
    }
  };

  if (!form) return <div>Loading form...</div>;

  return (
    <form onSubmit={handleSubmit}>
      <h2>{form.title}</h2>
      <p>{form.description}</p>
      {form.questions.map((question) => (
        <div key={question._id}>
          <label>{question.questionText}</label>
          {question.questionType === 'text' && (
            <input
              type="text"
              onChange={(e) => handleInputChange(question._id, e.target.value)}
            />
          )}
          {/* You can add other input types like multiple-choice, checkbox, etc. */}
        </div>
      ))}
      <button type="submit">Submit</button>
    </form>
  );
};

export default FormResponse;
